 CREATE VIEW [ViewOrdFabLanSubProdPVsSerInt] AS SELECT ORDFABLAN.OrdEmp, ORDFABLAN.OrdCod, ORDFABLANLEVEL4.CPSSeq                                 AS OrdCSInSeq, ORDFABLAN3.CPBCod                                      AS OrdCSInCPbCod, CAPRO_SUBPRODUTO.CPRONOM                               AS OrdCSInCPBDes, CAPROEPCAPROEPSIN.CSERCOD                              AS OrdCSInCPbCSerCod, CASIN.CSERNOM                                          AS OrdCSInCPbCSerNom, ORDFABLAN3.OrdCPBQtd                                   AS OrdCSInCPbQtd, ViewOrdFabLanSubProdPVsSerIntRomaneio.Op0QtdPrd        AS OrdCSInCPbQtdPrd, ViewOrdFabLanSubProdPVsSerIntRomaneio.Opr0QtdBai       AS OrdCSInCPbQtdBai, ViewOrdFabLanSubProdPVsSerIntRomaneio.Opr0QtdSal       AS OrdCSInCPbQtdSal, CAPRO_SUBPRODUTO.CPROPES                               AS OrdCSInCPbPesUni, ORDFABLAN3.OrdCPBQtd * CAPRO_SUBPRODUTO.CPROPES        AS OrdCSInCPbPesTot, ViewOrdFabLanSubProdPVsSerIntRomaneio.Opr0PesTotPrd    AS OrdCSInCPbPesTotPrd, ViewOrdFabLanSubProdPVsSerIntRomaneio.Opr0PesTotBai    AS OrdCSInCPbPesTotBai, ViewOrdFabLanSubProdPVsSerIntRomaneio.Opr0PesTotSal    AS OrdCSInCPbPesTotSal FROM ORDFABLAN3 INNER JOIN ORDFABLANLEVEL4 ON  ORDFABLAN3.OrdEmp         = ORDFABLANLEVEL4.OrdEmp AND ORDFABLAN3.OrdCod         = ORDFABLANLEVEL4.OrdCod INNER JOIN ORDFABLAN ON  ORDFABLAN3.OrdEmp         = ORDFABLAN.OrdEmp AND ORDFABLAN3.OrdCod         = ORDFABLAN.OrdCod INNER JOIN CAPRO AS CAPRO_SUBPRODUTO ON  ORDFABLAN3.CPBCod         = CAPRO_SUBPRODUTO.CPROCOD INNER JOIN CAPROEPCAPROEPSIN ON  ORDFABLANLEVEL4.CPSSeq    = CAPROEPCAPROEPSIN.CPSSeq AND ORDFABLAN.CPRO1EP         = CAPROEPCAPROEPSIN.CPRO1EP AND ORDFABLAN.CPROCOD         = CAPROEPCAPROEPSIN.CPROCOD INNER JOIN CASIN ON  CAPROEPCAPROEPSIN.CSERCOD = CASIN.CSERCOD LEFT OUTER JOIN ViewOrdFabLanSubProdPVsSerIntRomaneio ON  ORDFABLAN.OrdEmp          = ViewOrdFabLanSubProdPVsSerIntRomaneio.OPR1OPEmp AND ORDFABLAN.OrdCod          = ViewOrdFabLanSubProdPVsSerIntRomaneio.OPR1OPCod AND ORDFABLAN3.CPBCod         = ViewOrdFabLanSubProdPVsSerIntRomaneio.OPR1CPbCod AND CAPROEPCAPROEPSIN.CSERCOD = ViewOrdFabLanSubProdPVsSerIntRomaneio.OPR0CSerCod
GO
GRANT SELECT
    ON OBJECT::[dbo].[ViewOrdFabLanSubProdPVsSerInt] TO [interclick]
    AS [dbo];

